INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES ('44293', '1', '0', 'You\'re one of the fiercest-looking warriors I\'ve ever seen, $N !', '12', '0', '100', '1', '0', '0', '0', 'class_warrior');
