INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES ('44293', '3', '0', 'What\'s it like to be a shaman? I met Thrall once. My father doesn\'t trust him but he seemed very wise.', '12', '0', '100', '6', '0', '0', '44752', 'class_shaman');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES ('44293', '4', '0', 'This is great, I never get to talk to Stormwind\'s rogues. They\'re always so quiet and secretive...', '12', '0', '100', '1', '0', '0', '44751', 'class_rogues');
