INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES ('44293', '6', '0', 'You\'re a mage, like Jaina Proudmoore? How long have you studied the arcane? It seems so difficult to master.', '12', '0', '100', '6', '0', '0', '44748', 'class_mage');
